import React, { useState } from "react";
import "../styles/LoginSignup.css";
import { useNavigate } from "react-router-dom";

const LoginSignup = () => {
  const [action, setAction] = useState("Log In");
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const toggleForm = () => {
    setAction(action === "Log In" ? "Sign Up" : "Log In");
    setName("");
    setEmail("");
    setPassword("");
  };

  const handleLogin = async () => {
    console.log("Logging in with", { name, password });
    navigate("/Profile");
  };

  const handleRegister = async () => {
    console.log("Registering with", { name, email, password });
    toggleForm();
  };

  return (
    <div className="ls-container">
      <div className="ls-header">
        <div className="ls-text">{action}</div>
        <div className="ls-underline"></div>
      </div>
      <div className="ls-inputs">
        <div className="ls-input-login">
          <input
            type="text"
            placeholder="Username"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        {action === "Sign Up" && (
          <div>
            <div className="ls-input-login">
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="ls-input-login">
              <input
                type="phone-number"
                placeholder="Phone number"
                value={phone}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
          </div>
        )}
        <div className="ls-input-login">
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
      </div>
      <div className="ls-submit-container">
        <div
          className="ls-submit"
          onClick={() => {
            action === "Log In" ? handleLogin() : handleRegister();
          }}
        >
          {action}
        </div>
      </div>
      <div className="ls-toggle-action">
        {action === "Log In" ? (
          <p>
            Don't have an account? <span onClick={toggleForm}>Sign Up</span>
          </p>
        ) : (
          <p>
            Already have an account? <span onClick={toggleForm}>Sign In</span>
          </p>
        )}
      </div>
    </div>
  );
};

export default LoginSignup;
