import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "../styles/LakeDetails.css"; // Add your custom styles

const LakeDetails = () => {
  const { id } = useParams(); // Get the lake ID from the URL
  const [lake, setLake] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLakeDetails = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/lakes/${id}`);
        if (!response.ok) {
          throw new Error("Failed to fetch lake details");
        }
        const data = await response.json();
        setLake(data);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchLakeDetails();
  }, [id]); // Rerun the effect if the `id` changes

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!lake) {
    return <div>Loading...</div>;
  }

  return (
    <div className="lake-details-container">
      <h1>Hello world</h1>
      <h1>{lake.name}</h1>
      <p>{lake.description}</p>
      <p>Location: {lake.location}</p>
      {/* Add more information as needed */}
    </div>
  );
};

export default LakeDetails;
