import React, { useEffect, useState } from "react";
import "./LakesList.css";

const LakesList = () => {
  const [lakes, setLakes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("/api/lakes")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setLakes(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  const handleReservation = (lakeId) => {
    // Handle reservation (you can implement this functionality as needed)
    alert(`You have reserved lake with ID: ${lakeId}`);
  };

  if (loading) {
    return <div className="loading">Loading lakes...</div>;
  }

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  return (
    <div>
      <h1>Available Lakes for Reservation</h1>
      <div className="lake-container">
        {lakes.map((lake) => (
          <div className="lake-card" key={lake.id}>
            <h2>{lake.name}</h2>
            <p>Location: {lake.location}</p>
            <p>Area: {lake.area} kmÂ²</p>
            <p>Depth: {lake.depth} meters</p>
            <p>Water Type: {lake.waterType}</p>
            <button onClick={() => handleReservation(lake.id)}>Reserve</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LakesList;
